require('colors');
const { guardarDb, leerDB } = require('../helpers/guardar_archivo');
const { inquirerMenu, pausa, leerInput, listadoTareaBorrar, confirmar, mostrarListadoCheckList } = require('../helpers/inquirer');
const Tareas = require('../models/tareas');


console.clear()

const main = async () => {

    console.log('Hola mundo')
    let opt = '';
    const tareas = new Tareas();
    const tareasDB = leerDB();

    if (tareasDB) {
        tareas.cargarTareasFromArray(tareasDB);
    }



    do {
        // Imprimir el menú
        opt = await inquirerMenu();

        switch (opt) {
            case '1':
                // crear opcion
                const desc = await leerInput('Descripción:');
                tareas.crearTarea(desc);

                break;

            case '2':

                tareas.ListadoCompleto();
                break;
            case '3':
                tareas.ListarPendienteCompletadas(true);
                break;

            case '4':
                tareas.ListarPendienteCompletadas(false);
                break;

            case '5': //completado pediente
                const ids= await mostrarListadoCheckList(tareas.listadoArr); 
                tareas.toggleCompletadas(ids);
                break;
                
            case '6':

                const id = await listadoTareaBorrar(tareas.listadoArr);
                if (id !== '0') {
                    const ok = await confirmar('¿Estas seguro?')
                    if (ok) {
                        tareas.borrarTarea(id);
                        console.log('tarea borrada')
                    }

                }
                //Borrar menssaje

                break;
        }

        guardarDb(tareas.listadoArr);
        await pausa();

    } while (opt !== '0');

}

main()